/**
 * Detects whether the code includes a proper health-check endpoint,
 * such as `app.get('/health')` or `app.get('/status')`, commonly used in microservices.
 * 
 * Health endpoints are essential for system observability and uptime monitoring.
 * 
 * @param ast - Abstract Syntax Tree of the JavaScript/TypeScript file
 * @param file - Filename to help contextualize any reported issue
 * @returns string[] - Array of issue descriptions (if any)
 */
export function detectHealthCheckIssues(ast: any, file: string): string[] {
    const issues: string[] = []; // Container for storing issues
    let hasHealthCheck = false;  // Boolean flag to track health endpoint presence

    /**
     * Traverse the AST looking for `app.get('/health')` or `app.get('/status')`
     * This is a typical pattern for health-check routes in Node.js Express apps.
     */
    walkSimple(ast, {
        CallExpression(node) {
            // Check if the call is of the form: app.get(...)
            if (
                node.callee.type === 'MemberExpression' &&
                node.callee.object.type === 'Identifier' &&
                node.callee.object.name === 'app' &&
                node.callee.property.type === 'Identifier' &&
                node.callee.property.name === 'get'
            ) {
                // Retrieve the first argument of app.get(), which should be the route path
                const arg = node.arguments[0];

                // If the path is '/health' or '/status', we assume it's a valid health-check endpoint
                if (arg.type === 'Literal' && (arg.value === '/health' || arg.value === '/status')) {
                    hasHealthCheck = true;
                }
            }
        }
    });

    /**
     * If no health-check endpoint was found in this file, flag it as an issue.
     * Health-checks are vital for service discovery and load balancer integrations.
     */
    if (!hasHealthCheck) {
        issues.push(`${file} - No health-check endpoint detected.`);
    }

    return issues; // Return any issues found (or an empty list if none)
}

// Import the "ancestor" walker to analyze a node along with its hierarchical context in the AST
import { ancestor as walkAncestor } from "acorn-walk";

/**
 * Detects hardcoded dependencies inside classes or functions by inspecting 'new' expressions.
 * This check is useful for identifying violations of dependency injection principles.
 * 
 * @param ast - Parsed Abstract Syntax Tree (AST) of the code
 * @param file - The filename for tagging the detected issue
 * @returns string[] - List of identified dependency issues with descriptions
 */
export function detectDependencyIssues(ast: any, file: string): string[] {
    const issues: string[] = [];

    /**
     * Traverse the AST to find instances of 'new ClassName()'.
     * Using 'ancestor' traversal allows us to access parent nodes and understand context,
     * such as whether the instantiation is happening inside a class or function.
     */
    walkAncestor(ast, {
        NewExpression(node: any, ancestors: any[]) {
            // We're only interested in direct class constructors like `new Service()`, not computed or dynamic
            if (node.callee.type === 'Identifier') {

                /**
                 * Check if this `new` statement is declared within the scope of a class
                 * or any kind of function (declaration, expression, arrow).
                 * If so, it's likely a tightly-coupled dependency that violates clean architecture.
                 */
                const insideClassOrFunction = ancestors.some(
                    (ancestor) =>
                        ancestor.type === "ClassDeclaration" ||
                        ancestor.type === "FunctionDeclaration" ||
                        ancestor.type === "FunctionExpression" ||
                        ancestor.type === "ArrowFunctionExpression"
                );

                // If a hardcoded dependency is detected, report it with context
                if (insideClassOrFunction) {
                    issues.push(`${file} - Hardcoded dependency detected: ${node.callee.name}. Consider using dependency injection.`);
                }
            }
        }
    });

    return issues;
}

// Import the simple walker from acorn-walk for basic AST traversal
import { simple as walkSimple } from 'acorn-walk';

/**
 * Detects missing logging inside try-catch blocks.
 * Logging in catch blocks is a key practice for debugging and monitoring failures,
 * especially in production-grade microservices and applications.
 * 
 * @param ast - Abstract Syntax Tree representing the parsed source code
 * @param file - The file name being analyzed (used for reporting)
 * @returns string[] - List of issues describing missing logging cases
 */
export function detectLoggingIssues(ast: any, file: string): string[] {
    const issues: string[] = [];

    /**
     * Step 1: Traverse the AST to find all try-catch statements.
     * We focus on the `handler` block (i.e., the catch block) to check for logging usage.
     */
    walkSimple(ast, {
        TryStatement(node) {
            if (node.handler) {
                let hasLogging = false;

                /**
                 * Step 2: Inspect the contents of the catch block.
                 * We're looking for `console.log`, `console.error`, or any `console` method,
                 * which typically indicates that an error is at least being reported.
                 */
                walkSimple(node.handler.body, {
                    CallExpression(innerNode) {
                        if (
                            innerNode.callee.type === 'MemberExpression' &&
                            innerNode.callee.object.type === 'Identifier' &&
                            innerNode.callee.object.name === 'console' // Matches `console.log(...)`, etc.
                        ) {
                            hasLogging = true;
                        }
                    }
                });

                /**
                 * Step 3: If the catch block contains no logging, we flag it as a potential issue.
                 * This promotes better observability and debugging capability.
                 */
                if (!hasLogging) {
                    issues.push(`${file} - Missing logging in try-catch block.`);
                }
            }
        }
    });

    return issues;
}

